sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var glideRecord = new GlideRecord("x_58872_needit_needit");//x_58872_needit_
	glideRecord.addQuery('short_description','CONTAINS','e');
	glideRecord.query();

	var data = "Short Description \t Requsted for\n";
	if(glideRecord.next()) {
		//glideRecord.next();
		var desc =  glideRecord.getValue('short_description');
		var req = glideRecord.u_requested_for.name;
		data = data + desc + " \t\t\t " + req + "\n";
	}

	gs.info("Details is: \n" + data );
	var gr = new GlideRecord("x_58872_needit_needit");
	gr.query();
	gs.addInfoMessage("total count is " + gr.getRowCount());

	var ga = new GlideAggregate("x_58872_needit_needit");
	ga.addAggregate('COUNT');
	var rec = 0;
	ga.query();
	if(ga.next()) {
		rec = ga.getAggregate('COUNT');
	}
	gs.addInfoMessage("Total Count is " + rec);
	gs.debug("****sushil*****");
	gs.addInfoMessage("Debuggin " + gs.isDebugging());
})(current, previous);
