sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
	    g_form.clearOptions('u_what_needed');
		g_form.addOption('u_what_needed','legal 1','Legal 1');
		g_form.addOption('u_what_needed','legal 2', 'Legal 2');
		g_form.addOption('u_what_needed','other','other');
      return;
   }

   //Type appropriate comment here, and begin script below
	var whatneeded = g_form.getValue('u_what_needed');
	g_form.clearOptions('u_what_needed');

	if(newValue == 'hr') {
		g_form.addOption('u_what_needed','hr1','Human Resource');
		g_form.addOption('u_what_needed','hr2','Human Resource 2');
		g_form.addOption('u_what_needed','other','other');
	}  else if(newValue == 'facilities') {
		g_form.addOption('u_what_needed','facilities1','Facilities 1');
		g_form.addOption('u_what_needed','facilities2','Facilities 2');
		g_form.addOption('u_what_needed','other','other');
	} else if(newValue == 'legal') {
		g_form.addOption('u_what_needed','legal 1','Legal 1');
		g_form.addOption('u_what_needed','legal 2', 'Legal 2');
		g_form.addOption('u_what_needed','other','other');
	}
	var values = [];
var obj = {};


// works on the drop down fields
var deptData = g_form.getControl('u_what_needed');


// itertate through all the options and push the object containing its label and value in an array
for(var i = 0; i < deptData.options.length ; i++) {
 obj = {};
 obj.value = deptData.options[i].value;
 obj.label = deptData.options[i].label;
 values.push(obj);

}
// alert(	JSON.stringify(values));
}
